#!/usr/bin/env pwsh
$basedir=Split-Path $MyInvocation.MyCommand.Definition -Parent

$exe=""
$pathsep=":"
$env_node_path=$env:NODE_PATH
$new_node_path="F:\Mizuki博客\node_modules\.pnpm\@astrojs+check@0.9.4_typescript@5.9.2\node_modules\@astrojs\check\dist\node_modules;F:\Mizuki博客\node_modules\.pnpm\@astrojs+check@0.9.4_typescript@5.9.2\node_modules\@astrojs\check\node_modules;F:\Mizuki博客\node_modules\.pnpm\@astrojs+check@0.9.4_typescript@5.9.2\node_modules\@astrojs\node_modules;F:\Mizuki博客\node_modules\.pnpm\@astrojs+check@0.9.4_typescript@5.9.2\node_modules;F:\Mizuki博客\node_modules\.pnpm\node_modules"
if ($PSVersionTable.PSVersion -lt "6.0" -or $IsWindows) {
  # Fix case when both the Windows and Linux builds of Node
  # are installed in the same directory
  $exe=".exe"
  $pathsep=";"
} else {
  $new_node_path="/mnt/f/Mizuki博客/node_modules/.pnpm/@astrojs+check@0.9.4_typescript@5.9.2/node_modules/@astrojs/check/dist/node_modules:/mnt/f/Mizuki博客/node_modules/.pnpm/@astrojs+check@0.9.4_typescript@5.9.2/node_modules/@astrojs/check/node_modules:/mnt/f/Mizuki博客/node_modules/.pnpm/@astrojs+check@0.9.4_typescript@5.9.2/node_modules/@astrojs/node_modules:/mnt/f/Mizuki博客/node_modules/.pnpm/@astrojs+check@0.9.4_typescript@5.9.2/node_modules:/mnt/f/Mizuki博客/node_modules/.pnpm/node_modules"
}
if ([string]::IsNullOrEmpty($env_node_path)) {
  $env:NODE_PATH=$new_node_path
} else {
  $env:NODE_PATH="$new_node_path$pathsep$env_node_path"
}

$ret=0
if (Test-Path "$basedir/node$exe") {
  # Support pipeline input
  if ($MyInvocation.ExpectingInput) {
    $input | & "$basedir/node$exe"  "$basedir/../@astrojs/check/dist/bin.js" $args
  } else {
    & "$basedir/node$exe"  "$basedir/../@astrojs/check/dist/bin.js" $args
  }
  $ret=$LASTEXITCODE
} else {
  # Support pipeline input
  if ($MyInvocation.ExpectingInput) {
    $input | & "node$exe"  "$basedir/../@astrojs/check/dist/bin.js" $args
  } else {
    & "node$exe"  "$basedir/../@astrojs/check/dist/bin.js" $args
  }
  $ret=$LASTEXITCODE
}
$env:NODE_PATH=$env_node_path
exit $ret
